export default (state = [], action) => {
    switch (action.type) {
        case 'add-todo':
            return [
                ...state,
                Object.assign({}, {id: action.payload.id, todo: action.payload.todo})
            ];
        case 'remove-todo':
            return state.filter((data,i)=> i!==action.id);
        default:
            return state;
    }

}

//
// {
//     id: 0,
//         todo: "make some tea",
//     isComplete:false
// },
// {
//     id: 1,
//         todo: "do homework",
//     isComplete: false
// }
