import React from "react";
import {connect} from 'react-redux'
import * as actions from '../actions'
import {Button, Tab} from 'semantic-ui-react'
import '../css/todolist.css'

class TodoList extends React.Component {


    render() {
        console.log(this.props);


        const panes = [
            {
                menuItem: 'Todo-List',
                render: () =>
                    this.props.todoList.map((todo) =>
                        <Tab.Pane>
                            <div>

                                <p>{todo.todo}</p>
                                {!todo.isComplete && <Button content='Primary' primary onClick={() => {
                                    this.props.completeList(todo);
                                }}>
                                    Complete
                                    {todo.isCompletetrue}
                                </Button>}
                            </div>
                        </Tab.Pane>
                    )
            },
            {menuItem: 'Add-Todo', render: () => <Tab.Pane>Tab 2 Content</Tab.Pane>},
            {
                menuItem: 'Complete-List', render: () =>
                    this.props.completeListArray.map((completeElement) =>
                        <Tab.Pane>
                            <div>
                                <p>{completeElement.todo}</p>
                            </div>
                        </Tab.Pane>
                    )
            },
        ];


        const TabExampleLoading = () => <Tab panes={panes}/>;
        return (
            <div className="mytodo"><TabExampleLoading/></div>
        )

    }

}

const mapStateToProps = state => {
    return {
        todoList: state.TodoList,
        completeListArray: state.CompleteList
    }
};
export default connect(mapStateToProps, actions)(TodoList);
