export default (state = [], action) => {
    switch (action.type) {
        case 'add-todo':
            return [
                ...state,
                Object.assign({}, {id: action.payload.id, todo: action.payload.todo})
            ];
        case 'remove-todo':
            return removeObject(state,action.id);
        default:
            return state;
    }

}


const removeObject = (array, id) => {
    return array.filter((data, i) => i !== id);
};
