import React from "react";
import {connect} from 'react-redux'
import * as actions from '../actions'
import {Button, Tab} from 'semantic-ui-react'


class TodoList extends React.Component {


    render() {
        console.log(this.props);
        const panes = [
            {
                menuItem: 'Tab 1',
                render: this.props.todoList.map((todo) =>
                    <Tab.Pane>
                        <div>

                            <p>{todo.todo}</p>
                            <Button content='Primary' primary onClick={() => {
                                this.props.completeList(todo);
                            }}>
                                Complete
                            </Button>
                        </div>
                    </Tab.Pane>)
            },
            {menuItem: 'Tab 2', render: () => <Tab.Pane>Tab 2 Content</Tab.Pane>},
            {menuItem: 'Tab 3', render: () => <Tab.Pane>Tab 3 Content</Tab.Pane>},
        ];
        const TabExampleLoading = () => <Tab panes={panes}/>

        return (
            <TabExampleLoading panes={panes} />
            )
        ;
    }

}

const mapStateToProps = state => {
    return {
        todoList: state.TodoList,
        completeListArray: state.CompleteList
    }
};
export default connect(mapStateToProps, actions)(TodoList);
